import "./style.css";

const CATEGORIES = [
  { name: "technology", color: "#3b82f6" },
  { name: "science", color: "#16a34a" },
  { name: "finance", color: "#ef4444" },
  { name: "society", color: "#eab308" },
  { name: "entertainment", color: "#db2777" },
  { name: "health", color: "#14b8a6" },
  { name: "history", color: "#f97316" },
  { name: "news", color: "#8b5cf6" },
];

const initialFacts = [
  {
    id: 1,
    text: "React is being developed by Meta (formerly facebook)",
    source: "https://opensource.fb.com/",
    category: "technology",
    votesInteresting: 24,
    votesMindblowing: 9,
    votesFalse: 4,
    createdIn: 2021,
  },
  {
    id: 2,
    text: "Millennial dads spend 3 times as much time with their kids than their fathers spent with them. In 1982, 43% of fathers had never changed a diaper. Today, that number is down to 3%",
    source:
      "https://www.mother.ly/parenting/millennial-dads-spend-more-time-with-their-kids",
    category: "society",
    votesInteresting: 11,
    votesMindblowing: 2,
    votesFalse: 0,
    createdIn: 2019,
  },
  {
    id: 3,
    text: "Lisbon is the capital of Portugal",
    source: "https://en.wikipedia.org/wiki/Lisbon",
    category: "society",
    votesInteresting: 8,
    votesMindblowing: 3,
    votesFalse: 1,
    createdIn: 2015,
  },
];

function App() {
  return (
    <>
      {/* HEADER */ }
      <header className="header">
        <div className="logo">
          <img src="logo.png" height="68" width="68" alt="Today I Learned Logo" />
          <h1>Today I Learned</h1>
        </div>
        <button className="btn btn-large btn-open">Share a fact</button>
      </header>
      <NewFactForm />
      <main className="main">
        <CategoryFilter />
        <FactList />
      </main>
    </>
  );
}

function NewFactForm() {
  return <form className="fact-form">Fcat form</form>;
}

function CategoryFilter() {
  return <aside>Category filter</aside>;
}

function FactList() {
  //temporary
  const facts = initialFacts;
  return (
    <section><ul className="facts-list">
      { facts.map((fact) => <Fact factObj={ fact } />) }
    </ul></section>
  );
}

function Fact(props) {
  console.log(props);
  return (
    <li key={ props.factObj.id } className="fact">
      <p>
        { props.factObj.text }
        <a className="source" href={ props.factObj.source } target="_blank">[Source]</a>

      </p>
      <span className="tag" style={ { backgroundColor: CATEGORIES.find((cat) => cat.name === props.factObj.category).color } }>{ props.factObj.category }</span>
      <div className="vote-buttons">
        <button>üëç { props.factObj.votesInteresting }</button>
        <button>ü§Ø { props.factObj.votesMindblowing }</button>
        <button>‚õîÔ∏è { props.factObj.votesFalse }</button>
      </div>
    </li>
  );
}

export default App;
